import numpy as np
# 1 2 3 
# 4 5 6 
# 7 8 9

nbr = [''],[''],[''],[''],['']
#    1
#  2 3 4 
#5 6 7 8 9                  
#  A B C
#    D
# this keypad represented in a matrix
matris = np.zeros(shape=(7,7))  
matris[1] = [0,0,0,1,0,0,0]
matris[2] = [0,0,2,3,4,0,0]
matris[3] = [0,5,6,7,8,9,0]
matris[4] = [0,0,10,11,12,0,0]
matris[5] = [0,0,0,13,0,0,0]

#coordinates on keypad
xposition = 1            
yposition = 3       

for i in range(0,5):
    for letter in str(nbr[i]): 
        if letter == 'R' and matris[xposition+1,yposition] > 0:
            xposition += 1
        elif letter == 'L' and matris[xposition-1,yposition] > 0:
            xposition -= 1
        elif letter == 'U' and matris[xposition,yposition-1] > 0:
            yposition -= 1
        elif letter == 'D' and matris[xposition,yposition+1] > 0:
            yposition += 1  
    print('xposition' , xposition)
    print('yposition' , yposition)
    print(' ')
